export declare const gqlHome = "\nquery getHome($homeId:ID!) {\n    viewer {\n        home(id:$homeId) {\n            id\n            timeZone\n            appNickname\n            appAvatar \n            size\n            type\n            numberOfResidents\n            primaryHeatingSource\n            hasVentilationSystem\n            mainFuseSize\n            address {\n                address1\n                address2\n                address3\n                postalCode\n                city\n                country\n                latitude\n                longitude\n            }\n            owner {\n                id\n                firstName\n                isCompany\n                name\n                middleName\n                lastName\n                organizationNo\n                language\n                contactInfo {\n                    email\n                    mobile\n                }\n            }\n            meteringPointData {\n                consumptionEan\n                gridCompany\n                gridAreaCode\n                priceAreaCode\n                productionEan\n                energyTaxType\n                vatType\n                estimatedAnnualConsumption\n            }\n            features {\n                realTimeConsumptionEnabled\n            }\n        }\n    }\n}\n";
export declare const gqlHomeRealTime = "\nquery getHome($homeId:ID!) {\n    viewer {\n        home(id:$homeId) {\n            features {\n                realTimeConsumptionEnabled\n            }\n        }\n    }\n}\n";
